import React, { useEffect } from 'react';
import runServer from './server';
import useInput from './hooks/useInput';
import useSubmit from './hooks/useSubmit';
import useData from './hooks/useData';
import { Spinner, SuccessIcon, FailureIcon } from './icons';

// 2000ms delayed
runServer();

const App = () => {
  const { handleChange, inputValue } = useInput();
  const { loading, isSuccess, error, handleBlur } = useSubmit();
  const { fetchData } = useData();

  const showError = !loading && error;

  useEffect(() => {
    loading && fetchData();
  }, [fetchData, loading]);

  return (
    <>
      <div style={styles.inputRow}>
        <input
          onChange={handleChange}
          onBlur={handleBlur}
          // placeholder="type in secret"
          style={styles.input}
          value={inputValue}
        />
        <div>{loading && <Spinner />}</div>
        <div>{!loading && isSuccess && <SuccessIcon />}</div>
        <div>{showError && <FailureIcon />}</div>
      </div>

      <div>{showError && <div style={styles.error}>{error}</div>}</div>
    </>
  );
};

const styles = {
  inputRow: { display: 'flex', alignItems: 'center' },
  input: {
    border: '2px solid lightgray',
    outline: 'none',
    padding: 5,
  },
  error: {
    fontFamily: 'Verdana',
    fontSize: 10,
    paddingTop: 5,
  },
};

export default App;
