import React, { useEffect } from 'react';
import runServer from './server';
import useInput from './hooks/useInput';
import useSubmit from './hooks/useSubmit';
import useData from './hooks/useData';
// import { SpinnerRoundFilled } from 'spinners-react';
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
// import { faCheck, faTimesCircle } from '@fortawesome/free-solid-svg-icons';

// const successIcon = <FontAwesomeIcon icon={faCheck} />;
// const failureIcon = <FontAwesomeIcon icon={faTimesCircle} />;

// 2000ms delayed
runServer();

const App = () => {
  const { handleChange, inputValue } = useInput();
  const { loading, isSuccess, error, handleBlur } = useSubmit();
  const { fetchData } = useData();

  useEffect(() => {
    loading && fetchData();
  }, [fetchData, loading]);

  return (
    <>
      <div style={{ display: 'flex' }}>
        <input
          value={inputValue}
          onChange={handleChange}
          onBlur={handleBlur}
          style={styles.input}
        />
        <div>
          {loading && (
            <>
              <Spinne />
              {/* <SpinnerRoundFilled
                color="green"
                secondaryColor="gray"
                size="25"
                speed="150"
              /> */}
            </>
          )}
        </div>
      </div>
      <div>{!loading && isSuccess && <>{successIcon}</>}</div>
      <div>
        {!loading && error && (
          <>
            {failureIcon}
            {error}
          </>
        )}
      </div>
    </>
  );
};

const styles = {
  input: {
    border: '2px solid lightgray',
    outline: 'none',
    padding: 5,
  },
};

export default App;
