import React, { useState, useEffect } from 'react';

const App = () => {
  const [loading, setIsLoading] = useState(false);
  const [inputValue, setInputValue] = useState('');
  const [status, setStatus] = useState(null);

  const fetchData = async () => {
    const req = await fetch('http://localhost:8080/api/input');

    const json = await req.json();
    console.log(json);
    setInputValue(json.text);
  };

  useEffect(() => {
    fetchData();
  }, [loading]);

  const handleChange = (e) => {
    const { value } = e.target;
    setInputValue(value);
  };

  const handleBlur = async (e) => {
    const { value } = e.target;
    setIsLoading(true);
    await fetch('http://localhost:8080/api/input', {
      method: 'POST',
      body: JSON.stringify({ text: inputValue }),
    });
    setStatus('success');
  };

  return (
    <>
      <div style={{ display: 'flex' }}>
        <input value={inputValue} onChange={handleChange} onBlur={handleBlur} />
        {/* <div>{loading && <> loading... </>}</div> */}
      </div>
      {/* <div>{status === 'success' && <>tick</>}</div> */}
    </>
  );
};

export default App;
