import React, { useState, useEffect } from 'react';
import { Server } from 'miragejs';

let server = new Server({ timing: 2000 });
server.get('/api/input', {});
server.post('/api/input', (_, request) => {
  return JSON.parse(request.requestBody);
});

const App = () => {
  const [loading, setIsLoading] = useState(false);
  const [inputValue, setInputValue] = useState('');
  const [isSuccess, setIsSuccess] = useState(null);

  const fetchData = async () => {
    await fetch('/api/input').then((json) => {
      const data = JSON.parse(json._bodyInit);
      setInputValue(data.text);
    });
  };

  useEffect(() => {
    loading && fetchData();
  }, [loading]);

  const handleChange = (e) => {
    const { value } = e.target;
    setInputValue(value);
  };

  const handleBlur = async (e) => {
    const { value } = e.target;

    const variant = Math.random();
    if (variant <= 0.5) {
      // A Code
      console.log(variant, 'TRUE');
    } else {
      // C Code
      console.log(variant, 'FALSE');
    }

    // setIsLoading(true);
    // await fetch('/api/input', {
    //   method: 'POST',
    //   body: JSON.stringify({ input: value }),
    // });
    // setIsLoading(false);
    // setIsSuccess(true);
  };

  return (
    <>
      <div style={{ display: 'flex' }}>
        <input value={inputValue} onChange={handleChange} onBlur={handleBlur} />
        <div>{loading && <> loading... </>}</div>
      </div>
      <div>{isSuccess && <>&#9989;</>}</div>
    </>
  );
};

export default App;
