import React, { useState, useEffect } from 'react';
import { Server } from 'miragejs';

let server = new Server({ timing: 2000 });
server.get('/api/input', { input: 'Foobarbaz' });
server.post('/api/input', (schema, request) => {
  console.log(request);
  return JSON.parse(request.requestBody);
});

const App = () => {
  const [loading, setIsLoading] = useState(true);
  const [inputValue, setInputValue] = useState('');
  console.log(inputValue);

  const fetchData = async () => {
    await fetch('/api/input').then((json) => {
      console.log('json: ', json);
      setInputValue(json.input);
      setIsLoading(false);
    });
  };

  useEffect(() => {
    // setInputValue('Some initial text');
    fetchData();
  }, []);

  const handleChange = (e) => {
    const { value } = e.target;
    setInputValue(value);
  };

  const handleBlur = (e) => {
    const { value } = e.target;
    fetch('/api/input', {
      method: 'POST',
      body: JSON.stringify({ input: value }),
    });
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <input value={inputValue} onChange={handleChange} onBlur={handleBlur} />
    </div>
  );
};

export default App;
