import React, { useEffect } from 'react';
import runServer from './server';
import useInput from './hooks/useInput';
import useSubmit from './hooks/useSubmit';
import useData from './hooks/useData';
import { SpinnerRoundFilled } from 'spinners-react';
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
// import { faTick } from '@fortawesome/free-solid-svg-icons';
import 'font-awesome/css/font-awesome.min.css';

const successIcon = <i class="fas fa-check"></i>; //<FontAwesomeIcon icon={faTick} />;

// 2000ms delayed
runServer();

const App = () => {
  const { handleChange, inputValue } = useInput();
  const { loading, isSuccess, error, handleBlur } = useSubmit();
  const { fetchData } = useData();

  useEffect(() => {
    loading && fetchData();
  }, [fetchData, loading]);

  return (
    <>
      <div style={{ display: 'flex' }}>
        <input
          value={inputValue}
          onChange={handleChange}
          onBlur={handleBlur}
          style={styles.input}
        />
        <div>
          {loading && (
            <>
              <SpinnerRoundFilled
                color="green"
                secondaryColor="gray"
                size="25"
                speed="150"
              />
            </>
          )}
        </div>
      </div>
      <div>{!loading && isSuccess && <>foo</>}</div>
      <div>
        {!loading && error && (
          <>
            {successIcon}
            {error}
          </>
        )}
      </div>
    </>
  );
};

const styles = {
  input: {
    border: '2px solid lightgray',
    outline: 'none',
    padding: 5,
  },
};

export default App;
