import React, { useState, useEffect } from 'react';
import { Server } from 'miragejs';

let server = new Server({ timing: 1000 });
server.get('/api/input', { text: 'foo' });
// server.post('/api/input', (_, request) => {
//   return JSON.parse(request.requestBody);
// });

const App = () => {
  const [loading, setIsLoading] = useState(false);
  const [inputValue, setInputValue] = useState('');
  console.log(inputValue);

  const fetchData = async () => {
    await fetch('/api/input').then((json) => {
      console.log('json: ', JSON.parse(json._bodyInit));
      const data = JSON.parse(json._bodyInit);
      setInputValue(data.text);

      // setInputValue(json.input);
      // setIsLoading(false);
    });
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleChange = (e) => {
    const { value } = e.target;
    setInputValue(value);
  };

  // const handleBlur = (e) => {
  //   const { value } = e.target;
  //   setIsLoading(true);
  //   fetch('/api/input', {
  //     method: 'POST',
  //     body: JSON.stringify({ input: value }),
  //   });
  //   setIsLoading(false);
  // };

  return (
    <div>
      <input
        value={inputValue}
        onChange={handleChange}
        // onBlur={handleBlur}
      />
      {loading && <div>Loading...</div>}
      <div>{inputValue}</div>
    </div>
  );
};

export default App;
