import React, { useEffect } from 'react';
import runServer from './server';
import useInput from './hooks/useInput';
import useSubmit from './hooks/useSubmit';
import useData from './hooks/useData';
import { SpinnerRoundFilled } from 'spinners-react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCoffee } from '@fortawesome/free-solid-svg-icons';

const successIcon = <FontAwesomeIcon icon={faCoffee} />;

// 2000ms delayed
runServer();

const App = () => {
  const { handleChange, inputValue } = useInput();
  const { loading, isSuccess, error, handleBlur } = useSubmit();
  const { fetchData } = useData();

  useEffect(() => {
    loading && fetchData();
  }, [fetchData, loading]);

  return (
    <>
      <div style={{ display: 'flex' }}>
        <input
          value={inputValue}
          onChange={handleChange}
          onBlur={handleBlur}
          style={styles.input}
        />
        <div>
          {loading && (
            <>
              <SpinnerRoundFilled
                color="green"
                secondaryColor="gray"
                size="25"
                speed="150"
              />
              <FontAwesomeIcon icon={faCoffee} />
            </>
          )}
        </div>
      </div>
      <div>{!loading && isSuccess && <>{successIcon}</>}</div>
      <div>{!loading && error && <>&#10008;{error}</>}</div>
    </>
  );
};

const styles = {
  input: {
    border: '2px solid lightgray',
    outline: 'none',
    padding: 5,
  },
};

export default App;
