import { Server } from 'miragejs';

const BASE_PATH = '/api/input';

const runServer = () => {
  let server = new Server({ timing: 2000 });
  server.get(BASE_PATH, {});
  server.post(BASE_PATH, (_, request) => {
    return JSON.parse(request.requestBody);
  });
};

export default runServer;
